    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
    String nilainya_x = jTextField2.getText();
    String nilainya_a = jTextField3.getText();
    String nilainya_n = jTextField4.getText();
    double x = Double.parseDouble(nilainya_x);
    double a = Double.parseDouble(nilainya_a);
    double n = Double.parseDouble(nilainya_n);
    double f0;
    
            if (x<0){
                f0 = Math.log(-x);
                double hasil, hasilfganjil = 0, hasilfgenap = 0;
                    
            for (double i = 0; i<=n; i++){
                double fganjil = (Math.pow((x-a), ((2*i)+1))/ (Math.pow(x, ((2*i)+1)))*((2*i)+1));
                hasilfganjil = fganjil++;}
                    
            for (double j = 1; j<=n; j++){
                double fgenap = (Math.pow((x-a),(2*j)))/ (Math.pow(x, (2*j))*(2*j));
                hasilfgenap = fgenap++;}
                hasil = f0 + hasilfgenap - hasilfganjil;
                String output = Double.toString(hasil);
            jTextField5.setText(output);
                    
            }else if (x>=0){
            jTextField5.setText("Bilangan numerus dari persamaan tidak boleh kurang dari nol");     
        }
    }//GEN-LAST:event_jButton1ActionPerformed

