    private void jComboBox1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jComboBox1ActionPerformed
        String soal = (String) jComboBox1.getSelectedItem();
        switch (soal) {
            case "4":
            String soal4 = "f(x) = ln (-x)";
            jTextField1.setText(String.valueOf(soal4));
            break;
            case "5":
            String soal5 = "f(x) = x /(1-x)";
            jTextField1.setText(String.valueOf(soal5));
            break;
            case "1":
            String soal1 = "f(x) = (e^(x^2))";
            jTextField1.setText(String.valueOf(soal1));
            break;
        }
    }//GEN-LAST:event_jComboBox1ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
    String nilainya_x = jTextField2.getText();
    String nilainya_a = jTextField3.getText();
    String nilainya_n = jTextField4.getText();
    double x = Double.parseDouble(nilainya_x);
    double a = Double.parseDouble(nilainya_a);
    double n = Double.parseDouble(nilainya_n);
    double e = 2.718;
    double ne = Math.pow(e, Math.pow (x,2));
    double hasil2=0;
        
            for (double i = 0; i<=n; i++){
                double hasil1 = (Math.pow(2,i)*ne*Math.pow((x-a),i))/(faktorial ((int) i));
                hasil2 = hasil1++;
                String output = Double.toString(hasil2);
            jTextField5.setText(output);
        }       
    }//GEN-LAST:event_jButton4ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
    String nilainya_x = jTextField2.getText();
    String nilainya_a = jTextField3.getText();
    String nilainya_n = jTextField4.getText();
    double x = Double.parseDouble(nilainya_x);
    double a = Double.parseDouble(nilainya_a);
    double n = Double.parseDouble(nilainya_n);
            
            double [] fungsi = new double [100001];
            double es = 0.5 * Math.pow(10,2-n);
            fungsi [0] = x/(1-x);
            double p = 0;
            
            for (int i = 1; i <100000; i++){
                fungsi [i]= fungsi [0] + ((Math.pow(x-a,i))*(x)*(Math.pow(-1,i))/ (Math.pow(1-x,i+1)));
                double ea = ((fungsi[i]-fungsi[i-1])/fungsi[i])*100;
            if (Math.abs(ea)<(es)){
                p += fungsi[i];
                System.out.println(i);  
                }
            }
            jTextField5.setText(String.valueOf(p));
       
    }//GEN-LAST:event_jButton3ActionPerformed

